local addon = ...local topHeight = 40local botHeight = 48local width = UIParent:GetWidth()UIParent:SetPoint("TOPLEFT", 0, -topHeight)UIParent:SetPoint("BOTTOMRIGHT", 0, botHeight)local topSpace = CreateFrame("Frame", nil, UIParent)topSpace:SetAlpha(0.9)topSpace:SetFrameStrata("BACKGROUND")topSpace:SetFrameLevel(1)topSpace:SetSize(width, topHeight)topSpace:SetPoint("TOPLEFT", 0, topHeight)topSpace.bg = topSpace:CreateTexture()topSpace.bg:SetTexture("Interface\\AddOns\\"..addon.."\\space")topSpace.bg:SetTexCoord(0, 1, 1, 0)topSpace.bg:SetPoint("TOPLEFT")topSpace.bg:SetPoint("BOTTOMRIGHT")local botSpace = CreateFrame("Frame", nil, UIParent)botSpace:SetAlpha(0.95)botSpace:SetFrameStrata("BACKGROUND")botSpace:SetFrameLevel(1)botSpace:SetSize(width, botHeight)botSpace:SetPoint("BOTTOMLEFT", 0, -botHeight)botSpace.bg = botSpace:CreateTexture()botSpace.bg:SetTexture("Interface\\AddOns\\"..addon.."\\space")botSpace.bg:SetPoint("TOPLEFT")botSpace.bg:SetPoint("BOTTOMRIGHT")local shadow = CreateFrame("Frame", nil, UIParent)shadow:SetAlpha(0.8)shadow:SetFrameStrata("BACKGROUND")shadow:SetFrameLevel(0)shadow:SetPoint("TOPLEFT")shadow:SetPoint("BOTTOMRIGHT")shadow.bg = shadow:CreateTexture()shadow.bg:SetTexture("Interface\\AddOns\\"..addon.."\\shadow")shadow.bg:SetPoint("TOPLEFT")shadow.bg:SetPoint("BOTTOMRIGHT")