--------------------------------------- Tooltip美化-------------------------------------local function AeroTooltip(...)    local frame, mask    for i = 1, select("#", ...) do        frame = _G[select(i, ...)]        if (frame) then            mask = frame:CreateTexture("AeroTooltipMask")            mask:SetTexture("Interface\\Tooltips\\UI-Tooltip-Background")            mask:SetPoint("TOPLEFT", frame, "TOPLEFT", 4, -4)            mask:SetPoint("BOTTOMRIGHT", frame, "TOPRIGHT", -4, -30)            mask:SetBlendMode("ADD")            mask:SetGradientAlpha("VERTICAL", 0, 0, 0, 0, 1, 1, 1, 0.76)        end    endendAeroTooltip("GameTooltip")